import { ArrowLeft, Download, Mail, Home, Calendar, BookOpen, User, TrendingUp } from 'lucide-react';
import { Button } from './ui/button';
import { Card } from './ui/card';
import { toast } from 'sonner';
import { useEffect, useState } from 'react';

interface WeeklySummaryProps {
  onNavigate: (screen: any) => void;
}

export function WeeklySummary({ onNavigate }: WeeklySummaryProps) {
  const days = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
  const dates = [13, 14, 15, 16, 17, 18, 19];

  const [dbMeals, setDbMeals] = useState<any[]>([]);

  // ✅ FETCH MEALS FROM BACKEND
  useEffect(() => {
    const fetchMeals = async () => {
      const token = localStorage.getItem('token');
      if (!token) return;

      try {
        const res = await fetch('http://localhost:5050/api/meals', {
          headers: { Authorization: `Bearer ${token}` }
        });

        const data = await res.json();
        setDbMeals(data);
      } catch (error) {
        console.error('Failed to fetch weekly meals:', error);
      }
    };

    fetchMeals();
  }, []);

  // ✅ CALCULATE STATS FROM DATABASE
  const getWeekStats = () => {
    const weekDates = dates.map(d => `2024-10-${d}`);
    const weekLogs = dbMeals.filter(log => weekDates.includes(log.date));

    const totalMealsCount = weekLogs.reduce((sum, log) => {
      return sum + log.breakfast.length + log.lunch.length + log.dinner.length + log.snacks.length;
    }, 0);

    const totalCalories = weekLogs.reduce((sum, log) => {
      const dayCalories = [...log.breakfast, ...log.lunch, ...log.dinner, ...log.snacks]
        .reduce((daySum, meal) => daySum + meal.calories, 0);
      return sum + dayCalories;
    }, 0);

    const avgCalories = weekLogs.length > 0 ? Math.round(totalCalories / weekLogs.length) : 0;

    const allMeals = weekLogs.flatMap(log => [
      ...log.breakfast, ...log.lunch, ...log.dinner, ...log.snacks
    ]);

    const mealCounts: { [key: string]: number } = {};
    allMeals.forEach(meal => {
      mealCounts[meal.name] = (mealCounts[meal.name] || 0) + 1;
    });

    const mostFrequent =
      Object.entries(mealCounts).sort((a, b) => b[1] - a[1])[0]?.[0] || 'None';

    const topMeals = Object.entries(mealCounts)
      .sort((a, b) => b[1] - a[1])
      .slice(0, 2);

    return {
      totalMealsCount,
      avgCalories,
      mostFrequent,
      topMeals,
      daysWithMeals: weekLogs.length
    };
  };

  const stats = getWeekStats();

  const handleExportPDF = () => {
    const content = `
Plate Planner - Weekly Summary
October 13-19, 2024

Statistics:
- Total Meals: ${stats.totalMealsCount}
- Avg Calories: ${stats.avgCalories}
- Most Frequent: ${stats.mostFrequent}
- Days Logged: ${stats.daysWithMeals}/7
    `;

    const blob = new Blob([content], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'weekly-summary.txt';
    a.click();
    URL.revokeObjectURL(url);

    toast.success('Summary exported!');
  };

  const handleEmail = () => {
    const subject = encodeURIComponent('Plate Planner - Weekly Summary');
    const body = encodeURIComponent(JSON.stringify(stats, null, 2));
    window.location.href = `mailto:?subject=${subject}&body=${body}`;
  };

  return (
    <div className="w-full max-w-md bg-white rounded-2xl shadow-xl overflow-hidden">

      <div className="bg-gradient-to-r from-purple-600 to-purple-500 p-6 text-white">
        <button onClick={() => onNavigate('track-meals')} className="mb-4 flex items-center">
          <ArrowLeft className="mr-2" /> Back to Tracking
        </button>
        <h2>Weekly Summary</h2>
        <p>October 13-19, 2024</p>
      </div>

      <div className="p-6 space-y-6">
        <Card className="p-4 border">
          <div className="grid grid-cols-2 gap-4">
            <div>Total Meals: {stats.totalMealsCount}</div>
            <div>Avg Calories: {stats.avgCalories}</div>
            <div>Most Frequent: {stats.mostFrequent}</div>
            <div>Days Logged: {stats.daysWithMeals}/7</div>
          </div>
        </Card>

        <div>
          <h3>Top Meals This Week</h3>
          {stats.topMeals.length ? stats.topMeals.map(([meal, count]: any) => (
            <Card key={meal} className="p-3 border">
              {meal} — {count} times
            </Card>
          )) : <p>No meals this week</p>}
        </div>

        <div className="grid grid-cols-2 gap-3">
          <Button onClick={handleExportPDF}>
            <Download /> Export
          </Button>
          <Button onClick={handleEmail}>
            <Mail /> Email
          </Button>
        </div>
      </div>

      <div className="border-t p-4 flex justify-around">
        <button onClick={() => onNavigate('home')}><Home /></button>
        <button onClick={() => onNavigate('track-meals')}><Calendar /></button>
        <button onClick={() => onNavigate('recipe')}><BookOpen /></button>
        <button onClick={() => onNavigate('profile')}><User /></button>
      </div>

    </div>
  );
}
