import { ArrowLeft } from 'lucide-react';
import { Logo } from './Logo';
import { useState } from 'react';
import { useApp } from '../context/AppContext';

interface CreateAccountScreenProps {
  onNavigate: (screen: any) => void;
}

export function CreateAccountScreen({ onNavigate }: CreateAccountScreenProps) {
  const { setUser } = useApp();
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    username: '',
    password: ''
  });
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');

  const validateEmail = (email: string): boolean => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

const handleCreateAccount = async () => {
  // Validate required fields
  if (!formData.firstName || !formData.lastName || !formData.email || !formData.password) {
    alert("Please fill all required fields");
    return;
  }

  // Validate email format
  if (!validateEmail(formData.email)) {
    setEmailError("Please enter a valid email address (e.g., user@example.com)");
    alert("Please enter a valid email address");
    return;
  }

  setEmailError('');

  // Validate password strength
  if (formData.password.length < 6) {
    setPasswordError("Password must be at least 6 characters long");
    alert("Password must be at least 6 characters long");
    return;
  }

  setPasswordError('');

  try {
    const response = await fetch("http://localhost:5050/api/auth/register", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        name: `${formData.firstName} ${formData.lastName}`,
        email: formData.email,
        password: formData.password,
        firstName: formData.firstName,
        lastName: formData.lastName
      })
    });

    const data = await response.json();

    if (!response.ok) {
      alert(data.error || "Account creation failed");
      return;
    }

    // Validate response data
    if (!data.token || !data.userId || !data.user) {
      alert("Invalid response from server");
      return;
    }

    // Clear old data and store new authentication data
    localStorage.clear();
    localStorage.setItem("token", data.token);
    localStorage.setItem("userId", data.userId);
    localStorage.setItem("user", JSON.stringify(data.user));

    // Set user in context with complete data from backend
    setUser({
      _id: data.userId,
      username: data.user.email,
      email: data.user.email,
      firstName: data.user.firstName,
      lastName: data.user.lastName,
      allergies: data.user.allergies || [],
      dietaryRestrictions: data.user.dietaryRestrictions || [],
      token: data.token
    });

    alert("Account created successfully!");
    onNavigate("onboarding");

  } catch (error: any) {
    console.error("Registration error:", error);
    alert("Cannot connect to server. Please try again.");
  }
};


  return (
    <div className="w-full max-w-md bg-white rounded-2xl shadow-xl p-8 md:p-12">
      <button 
        onClick={() => onNavigate('welcome')}
        className="mb-6 flex items-center text-gray-600 hover:text-gray-900 transition-colors"
      >
        <ArrowLeft className="w-5 h-5 mr-2" />
        Back
      </button>
      
      <div className="flex flex-col items-center text-center space-y-6 mb-8">
        <div className="w-16 h-16 bg-white rounded-2xl flex items-center justify-center shadow-lg">
          <Logo size="md" />
        </div>
        
        <div className="space-y-2">
          <h2 className="text-gray-900">Create Account</h2>
          <p className="text-gray-500">Join Plate Planner today</p>
        </div>
      </div>
      
      <div className="space-y-4">
        <div className="grid grid-cols-2 gap-4">
          <div className="space-y-2">
            <label htmlFor="firstName" className="block text-sm text-gray-700">First Name</label>
            <input 
              id="firstName"
              type="text" 
              placeholder="John"
              value={formData.firstName}
              onChange={(e) => setFormData({...formData, firstName: e.target.value})}
              className="w-full h-12 rounded-xl border border-gray-300 px-4 focus:outline-none focus:ring-2 focus:ring-purple-500"
            />
          </div>
          <div className="space-y-2">
            <label htmlFor="lastName" className="block text-sm text-gray-700">Last Name</label>
            <input 
              id="lastName"
              type="text" 
              placeholder="Doe"
              value={formData.lastName}
              onChange={(e) => setFormData({...formData, lastName: e.target.value})}
              className="w-full h-12 rounded-xl border border-gray-300 px-4 focus:outline-none focus:ring-2 focus:ring-purple-500"
            />
          </div>
        </div>
        
        <div className="space-y-2">
          <label htmlFor="email" className="block text-sm text-gray-700">Email *</label>
          <input 
            id="email"
            type="email" 
            placeholder="your@email.com"
            value={formData.email}
            onChange={(e) => {
              setFormData({...formData, email: e.target.value});
              if (emailError) setEmailError('');
            }}
            className={`w-full h-12 rounded-xl border px-4 focus:outline-none focus:ring-2 ${
              emailError ? 'border-red-500 focus:ring-red-500' : 'border-gray-300 focus:ring-purple-500'
            }`}
          />
          {emailError && (
            <p className="text-sm text-red-600">{emailError}</p>
          )}
        </div>
        
        <div className="space-y-2">
          <label htmlFor="username" className="block text-sm text-gray-700">Username (Optional)</label>
          <input 
            id="username"
            type="text" 
            placeholder="johndoe"
            value={formData.username}
            onChange={(e) => setFormData({...formData, username: e.target.value})}
            className="w-full h-12 rounded-xl border border-gray-300 px-4 focus:outline-none focus:ring-2 focus:ring-purple-500"
          />
        </div>
        
        <div className="space-y-2">
          <label htmlFor="password" className="block text-sm text-gray-700">Password * (min 6 characters)</label>
          <input 
            id="password"
            type="password" 
            placeholder="••••••••"
            value={formData.password}
            onChange={(e) => {
              setFormData({...formData, password: e.target.value});
              if (passwordError) setPasswordError('');
            }}
            className={`w-full h-12 rounded-xl border px-4 focus:outline-none focus:ring-2 ${
              passwordError ? 'border-red-500 focus:ring-red-500' : 'border-gray-300 focus:ring-purple-500'
            }`}
          />
          {passwordError && (
            <p className="text-sm text-red-600">{passwordError}</p>
          )}
        </div>
      </div>

      <button
        onClick={handleCreateAccount}
        className="w-full bg-gradient-to-r from-purple-600 to-purple-500 hover:from-purple-700 hover:to-purple-600 text-white py-6 rounded-xl shadow-lg mt-6 transition-colors"
      >
        Create Account
      </button>
      
      <div className="text-center mt-6 text-sm text-gray-600">
        Already have an account?{' '}
        <button 
          onClick={() => onNavigate('login')}
          className="text-purple-600 hover:text-purple-700"
        >
          Sign in
        </button>
      </div>
    </div>
  );
}
