import { ArrowLeft, Plus, Home, Calendar, BookOpen, User, X } from 'lucide-react';
import { Button } from './ui/button';
import { Card } from './ui/card';
import { Input } from './ui/input';
import { useState, useEffect } from 'react';
import { useApp, DailyMealLog, LoggedMeal } from '../context/AppContext';
import { toast } from 'sonner';

interface TrackMealsProps {
  onNavigate: (screen: any) => void;
}

export function TrackMeals({ onNavigate }: TrackMealsProps) {
  const { dailyMealLogs } = useApp();
  const [selectedDay, setSelectedDay] = useState(16);
  const days = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
  const dates = [13, 14, 15, 16, 17, 18, 19];

  const getCurrentDateString = () => `2024-10-${selectedDay}`;

  const emptyLog: DailyMealLog = {
    date: getCurrentDateString(),
    breakfast: [],
    lunch: [],
    dinner: [],
    snacks: []
  };

  const [todayMeals, setTodayMeals] = useState<DailyMealLog>(emptyLog);

  const [breakfastInput, setBreakfastInput] = useState({ name: '', calories: '' });
  const [lunchInput, setLunchInput] = useState({ name: '', calories: '' });
  const [dinnerInput, setDinnerInput] = useState({ name: '', calories: '' });
  const [snacksInput, setSnacksInput] = useState({ name: '', calories: '' });

  const [showBreakfastInput, setShowBreakfastInput] = useState(false);
  const [showLunchInput, setShowLunchInput] = useState(false);
  const [showDinnerInput, setShowDinnerInput] = useState(false);
  const [showSnacksInput, setShowSnacksInput] = useState(false);

  // ✅ LOAD MEALS FROM DATABASE FOR LOGGED-IN USER
  useEffect(() => {
    async function loadMealsFromDB() {
      try {
        const response = await fetch('http://localhost:5050/api/meals', {
          headers: {
            Authorization: localStorage.getItem('token') || ''
          }
        });

        const data = await response.json();

        const grouped: DailyMealLog = {
          date: getCurrentDateString(),
          breakfast: [],
          lunch: [],
          dinner: [],
          snacks: []
        };

        data.forEach((meal: any) => {
          const formatted = { name: meal.recipeName, calories: meal.calories };

          if (meal.date === getCurrentDateString()) {
            if (meal.mealType === 'Breakfast') grouped.breakfast.push(formatted);
            if (meal.mealType === 'Lunch') grouped.lunch.push(formatted);
            if (meal.mealType === 'Dinner') grouped.dinner.push(formatted);
            if (meal.mealType === 'Snacks') grouped.snacks.push(formatted);
          }
        });

        setTodayMeals(grouped);
      } catch (error) {
        toast.error('Failed to load meals from server');
      }
    }

    loadMealsFromDB();
  }, [selectedDay]);

  // ✅ SAVE MEAL TO DATABASE
  const saveMealToDB = async (mealType: string, name: string, calories: number) => {
    try {
      await fetch('http://localhost:5050/api/meals', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: localStorage.getItem('token') || ''
        },
        body: JSON.stringify({
          mealType,
          recipeName: name,
          calories,
          date: getCurrentDateString()
        })
      });
    } catch {
      toast.error('Failed to save meal to server');
    }
  };

  const addMeal = (mealType: 'breakfast' | 'lunch' | 'dinner' | 'snacks') => {
    const map: any = {
      breakfast: breakfastInput,
      lunch: lunchInput,
      dinner: dinnerInput,
      snacks: snacksInput
    };

    const setter: any = {
      breakfast: setBreakfastInput,
      lunch: setLunchInput,
      dinner: setDinnerInput,
      snacks: setSnacksInput
    };

    const input = map[mealType];
    const setInput = setter[mealType];

    if (!input.name.trim()) return toast.error('Please enter meal name');

    const calories = input.calories ? parseInt(input.calories) : 0;

    const newMeal: LoggedMeal = { name: input.name, calories };

    setTodayMeals(prev => ({
      ...prev,
      [mealType]: [...prev[mealType], newMeal]
    }));

    saveMealToDB(mealType.charAt(0).toUpperCase() + mealType.slice(1), input.name, calories);

    setInput({ name: '', calories: '' });
    toast.success(`${input.name} added `);
  };

  const calculateTotalCalories = () => {
    return [...todayMeals.breakfast, ...todayMeals.lunch, ...todayMeals.dinner, ...todayMeals.snacks]
      .reduce((sum, meal) => sum + meal.calories, 0);
  };

  return (
    <div className="w-full max-w-md bg-white rounded-2xl shadow-xl overflow-hidden">
      <div className="bg-gradient-to-r from-purple-600 to-purple-500 p-6 text-white">
        <button onClick={() => onNavigate('home')}>
          <ArrowLeft className="w-5 h-5 mr-2 inline" /> Back to Home
        </button>
      </div>

      <div className="p-6 space-y-6">
        <div className="text-gray-700 font-semibold">
          Today's Meals - {calculateTotalCalories()} cal
        </div>

        {(['breakfast', 'lunch', 'dinner', 'snacks'] as const).map(meal => (
          <Card key={meal} className="p-4">
            <div className="flex justify-between items-center mb-2">
              <span className="capitalize">{meal}</span>
              <button onClick={() => addMeal(meal)} className="bg-purple-500 text-white p-2 rounded-lg">
                <Plus className="w-4 h-4" />
              </button>
            </div>

            {todayMeals[meal].map((m, i) => (
              <div key={i} className="flex justify-between mb-1">
                <span>{m.name}</span>
                <span>{m.calories} cal</span>
              </div>
            ))}
          </Card>
        ))}
      </div>

      <div className="border-t border-gray-200 bg-white px-6 py-4">
        <div className="flex justify-around">
          <button onClick={() => onNavigate('home')}><Home /></button>
          <button onClick={() => onNavigate('track-meals')}><Calendar /></button>
          <button onClick={() => onNavigate('recipe')}><BookOpen /></button>
          <button onClick={() => onNavigate('profile')}><User /></button>
        </div>
      </div>
    </div>
  );
}
